steps.codeSnippet:
body {
    font-family: Arial, sans-serif;
    background-color: #f0f0f0;
    text-align: center;
}
h1 {
    color: #333333;
}
p {
    color: #666666;
}

// Example: Change text color when clicked
var paragraph = document.querySelector('p');
paragraph.addEventListener('click', function() {
    paragraph.style.color = 'blue';
});
